import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import Intro from "./pages/Intro";

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [gameName, setGameName] = useState("");
  const [serverName, setServerName] = useState("");
  const [className, setClassName] = useState("");
  const [remarks, setRemarks] = useState("");

  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          //const networkId = await web3.eth.net.getId();
          //const deployedNetwork = ___.networks[networkId];
          const instance = new web3.eth.Contract();
          //___.abi,
          //deployedNetwork && deployedNetwork.address
          setAccount(accounts[0]);
          setContract(instance);
          getInfo(instance);
        } catch (error) {}
      }
    };
    initWeb3();
  }, []);

  const getInfo = async (contract) => {
    const blockCount = await contract.methods.blockCount().call();
    const gotInfos = [];
    for (let i = 1; i <= blockCount; i++) {
      const block = await contract.methods.getInfo(i).call();
      gotInfos.push(block);
    }
    setBlocks(gotInfos);
  };

  const insertInfo = async () => {
    await contract.methods
      .insertInfo(gameName, serverName, className, remarks)
      .send({ from: account, gas: 3000000 });
    getInfo(contract);
  };
  return (
    <div>
      <Header />
      <ul>
        {blocks.map((block) => (
          <li key={block.id}>
            {block.gameName} - {block.serverName} - {block.className} -{" "}
            {block.remarks}
          </li>
        ))}
      </ul>

      <p><input type="text" onChange={(e) => setGameName(e.target.value)} placeholder="게임 이름" /></p>
      <p><input type="text" onChange={(e) => setServerName(e.target.value)} placeholder="서버 이름" /></p>
      <p><input type="text" onChange={(e) => setClassName(e.target.value)} placeholder="클래스 이름" /></p>
      <p><textarea rows="10" cols="50" onChange={(e) => setRemarks(e.target.value)} placeholder="100byte 제한" /></p>
     <p><button onClick={insertInfo}>자료 입력/블록 생성</button></p>
    <Footer />
    </div>
  );
};

export default App;
