import React, { useState } from "react";

export function ChatBTN() {
  const [nickName, setNickName] = useState(""); // 상태로 nickName 관리

  // 예시로 nickName을 업데이트하는 코드 추가
  const handleChange = (e) => {
    setNickName(e.target.value);
  };

  return (
    <div className="flo-btn-chat">
      <form action="http://192.168.0.90:8000" method="POST">
        <input
          type="hidden"
          name="user"
          value={nickName}
          onChange={handleChange} // 예시로 nickname 업데이트
        />
        <button className="flo flo-chat" type="submit"></button>
      </form>
    </div>
  );
}
