import React, { useState, useEffect } from "react";
import Web3 from "web3";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';  // React Router 임포트
import Header from "./pages/Header";  // Header 컴포넌트 임포트
import Footer from "./pages/Footer";  // Footer 컴포넌트 임포트
import { ChatBTN, HelpBTN } from "./pages/Buttons";  // 버튼 컴포넌트 임포트
import Search from './pages/Search';  // Search 페이지 컴포넌트 임포트
import Intro from './pages/Intro';  // Intro 페이지 컴포넌트 임포트
import './App.css';  // 스타일 시트 임포트

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [gameName, setGameName] = useState("");
  const [serverName, setServerName] = useState("");
  const [className, setClassName] = useState("");
  const [remarks, setRemarks] = useState("");

  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          //const networkId = await web3.eth.net.getId();
          //const deployedNetwork = ___.networks[networkId];
          const instance = new web3.eth.Contract();
          //___.abi,
          //deployedNetwork && deployedNetwork.address
          setAccount(accounts[0]);
          setContract(instance);
          getInfo(instance);
        } catch (error) {}
      }
    };
    initWeb3();
  }, []);

  const getInfo = async (contract) => {
    const blockCount = await contract.methods.blockCount().call();
    const gotInfos = [];
    for (let i = 1; i <= blockCount; i++) {
      const block = await contract.methods.getInfo(i).call();
      gotInfos.push(block);
    }
    setBlocks(gotInfos);
  };

  const insertInfo = async () => {
    await contract.methods
      .insertInfo(gameName, serverName, className, remarks)
      .send({ from: account, gas: 3000000 });
    getInfo(contract);
  };
  return (
    <Router>
      <div className="main-container">
        <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" />
        <Header />
        <Routes>
          <Route path="/" element={<Intro />} />
          <Route path="/App" element={<App />} />
          <Route path="/search" element={<Search />} />
        </Routes>

        <div className="block-create">
          <ul>
            {blocks.map((block, index) => (
              <li key={index}>
                {block.gameName} - {block.serverName} - {block.className} - {block.remarks}
              </li>
            ))}
          </ul>

          <div className="contents-box">
            <table className="main-contents">
              <tbody>
                <tr>
                  <td className="con-title">
                    <span className="input-group-text" id="basic-addon1">게임명</span>
                  </td>
                  <td className="con-text">
                    <input
                      type="text"
                      className="form-control"
                      onChange={(e) => setGameName(e.target.value)}
                      placeholder="ex)로스트아크"
                    />
                  </td>
                  <td rowSpan="3">
                    <span className="input-group-text">Note</span>
                    <textarea
                      className="form-control"
                      rows="5"
                      onChange={(e) => setRemarks(e.target.value)}
                      placeholder="100byte 제한"
                    />
                  </td>
                </tr>
                <tr>
                  <td className="con-title">
                    <span className="input-group-text" id="basic-addon1">서버명</span>
                  </td>
                  <td className="con-text">
                    <input
                      type="text"
                      className="form-control"
                      onChange={(e) => setServerName(e.target.value)}
                      placeholder="ex)루페온"
                    />
                  </td>
                </tr>
                <tr>
                  <td className="con-title">
                    <span className="input-group-text" id="basic-addon1">클래스</span>
                  </td>
                  <td className="con-text">
                    <input
                      type="text"
                      className="form-control"
                      onChange={(e) => setClassName(e.target.value)}
                      placeholder="ex)슬레이어"
                    />
                  </td>
                </tr>
                <tr>
                  <td colSpan="3">
                    <div className="d-grid gap-2">
                      <button className="btn btn-outline-secondary" type="button" onClick={insertInfo}>
                        자료 입력/블록 생성
                      </button>
                    </div>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>

        <ChatBTN />
        <HelpBTN />
        <Footer />
      </div>
    </Router>
  );
};

export default App;
