import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { History } from "./pages/History";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import './App.css';
import { Search } from "./pages/Search";
import IdProject from "./IdProject.json";

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [nickname, setNickname] = useState("");
  const [gameName, setGameName] = useState("");
  const [serverName, setServerName] = useState("");
  const [className, setClassName] = useState("");
  const [remarks, setRemarks] = useState("");

  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          if (accounts.length > 0) {
            const account = accounts[0];  // 유효한 account 주소 가져오기
            setAccount(account);
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = IdProject.networks[networkId];
            if (!deployedNetwork) {
              alert("Contract가 현재 네트워크에 배포되지 않았습니다.");
              return;
            }
            const instance = new web3.eth.Contract(IdProject.abi, deployedNetwork.address);
            setContract(instance);
            getInfo(instance);  // 올바른 주소와 계약 객체 전달
          } else {
            console.error("No accounts found.");
          }
        } catch (error) {
          console.error("Error initializing web3:", error);
        }
      } else {
        console.error("Ethereum object not found. Please install MetaMask.");
      }
    };
    initWeb3();
  }, []);

  useEffect(() => {
    console.log("Account:", account);
    console.log("Contract:", contract);
  }, [account, contract]); // account와 contract가 업데이트될 때마다 로그 출력


  const getInfo = async (contract) => {
    try {
      const blockCount = await contract.methods.blockCount().call();
      const gotInfos = [];
      for (let i = 1; i <= blockCount; i++) {
        const block = await contract.methods.getInfo(account, i).call();  // account 주소와 blockId (i)를 전달
        gotInfos.push(block);
      }
      setBlocks(gotInfos);
    } catch (error) {
      console.error("Error getting info:", error);
    }
  };

  const insertInfo = async () => {
    if (!contract) {
      alert("컨트랙트가 초기화되지 않았습니다. 네트워크를 확인해주세요.");
      return;
    }
  /*
    if (!nickname || !gameName || !serverName || !className) {
      alert("모든 필드를 입력하세요.");
      return;
    }
  */
    try {
      await contract.methods
        .insertInfo(nickname, gameName, serverName, className, remarks)
        .send({ from: account, gas: 5000000 });
      alert("블록 정보가 성공적으로 추가되었습니다.");
      getInfo(contract); // 블록 정보 다시 가져오기
    } catch (error) {
      console.error("Error inserting info:", error);
      alert("정보를 추가하는 중 오류가 발생했습니다.");
    }
  };




  return (
    <div className="main-container">
      <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH" crossorigin="anonymous" />
      <Header />
      <Search />
      <div className="block-create">
      <ul>
        {blocks.map((block) => (
          <li key={block.id}>
            {block.gameName} - {block.serverName} - {block.className} -{" "}
            {block.remarks}
          </li>
        ))}
      </ul><div className="contents-box">

      <table class="main-contents">
  <tr>
    <td class="con-title"><span class="input-group-text" id="basic-addon1">닉네임</span></td>
    <td class="con-text"><input type="text" class="form-control" onChange={(e) => 
      setGameName(e.target.value)} /></td>
    <td rowspan="4">
    <span class="input-group-text">Note</span>
    <textarea class="form-control" aria-label="With textarea" rows="7" cols="50" onChange={(e) =>
       setRemarks(e.target.value)} placeholder="100byte 제한" />
    </td>
  </tr>
  <tr>
  <td class="con-title"><span class="input-group-text" id="basic-addon1">게임명</span></td>
    <td class="con-text"><input type="text" class="form-control" onChange={(e) => 
      setGameName(e.target.value)} placeholder="ex)로스트아크" /></td>
  </tr>
  <tr>
    <td class="con-title"><span class="input-group-text" id="basic-addon1">서버명</span></td>
    <td class="con-text"><input type="text" class="form-control" onChange={(e) => 
      setServerName(e.target.value)} placeholder="ex)루페온" /></td>
  </tr>
  <tr>
    <td class="con-title"><span class="input-group-text" id="basic-addon1">클래스</span></td>
    <td class="con-text"><input type="text" class="form-control" onChange={(e) => 
      setClassName(e.target.value)} placeholder="ex)슬레이어" /></td>
  </tr>
  <tr>
    <td colspan="3">
    <div class="d-grid gap-2">
  <button class="btn btn-outline-secondary" type="button" onClick={insertInfo} >
  자료 입력/블록 생성</button>
</div></td>
  </tr>
</table></div></div>
     <History/>
     <ChatBTN/><HelpBTN />
     <Footer />
    </div>
  );
};

export default App;
