  return (
    <div>
      <div className="main-container-int">
        <div className="main-img">
          <img src="/main02.png" alt="main" />
        </div>
        <div className="btn-group" role="group" aria-label="Basic mixed styles example">
          <Link to="/#">
            <button type="button" className="btn btn-tutorial" />
          </Link>
          <button type="button" className="btn btn-start" onClick={handleStartClick} />
        </div>
      </div>
      <ChatBTN />
      <HelpBTN />
    </div>
  );
};

export default Intro;
