import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { History } from "./pages/History";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import "./App.css";
import { Search } from "./pages/Search";
import IdProject from "./IdProject.json";
import CreateBlock from "./pages/CreateBlock";
import Stats from "./pages/Stats";

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]); // Î∏îÎ°ù ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [selectedUser, setSelectedUser] = useState(null); // ÏÑ†ÌÉùÎêú ÏÇ¨Ïö©Ïûê (ÎãâÎÑ§ÏûÑ, Ï£ºÏÜå Ìè¨Ìï®)
  const [searchedBlock, setSearchedBlocks] = useState([]); // Í≤ÄÏÉâÎêú Î∏îÎ°ù ÏÉÅÌÉú
  const [nickName, setNickName] = useState("");

  // üîπ MetaMask Ïó∞Í≤∞ Î∞è Ïä§ÎßàÌä∏ Í≥ÑÏïΩ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          if (accounts.length > 0) {
            const account = accounts[0];
            setAccount(account);
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = IdProject.networks[networkId];
            const instance = new web3.eth.Contract(IdProject.abi, deployedNetwork.address);
            setContract(instance);

            // ‚úÖ Í≥ÑÏ†ïÏùò Î∏îÎ°ù Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò¥
            fetchBlocks(instance, account);
          } else {
            console.error("MetaMask Í≥ÑÏ†ïÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          }
        } catch (error) {
          console.error("Web3 Ï¥àÍ∏∞Ìôî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
          alert("MetaMaskÏôÄÏùò Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. ÌôïÏù∏Ìï¥ Ï£ºÏÑ∏Ïöî.");
        }
      } else {
        console.error("Ethereum object not found. Please install MetaMask.");
        alert("MetaMaskÍ∞Ä ÏÑ§ÏπòÎêòÏñ¥ ÏûàÏßÄ ÏïäÏäµÎãàÎã§. MetaMaskÎ•º ÏÑ§ÏπòÌï¥ Ï£ºÏÑ∏Ïöî.");
      }
    };

    initWeb3();

    // üîπ ÏÉàÎ°úÍ≥†Ïπ® Ïãú Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Î∏îÎ°ù Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
    const savedBlocks = localStorage.getItem("blocks");
    if (savedBlocks) {
      setBlocks(JSON.parse(savedBlocks));
    }
  }, []);

  // üîπ blocks ÏÉÅÌÉú Î≥ÄÍ≤Ω Ïãú Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê Ï†ÄÏû•
  useEffect(() => {
    if (blocks.length > 0) {
      localStorage.setItem("blocks", JSON.stringify(blocks));
    }
  }, [blocks]);

  // üîπ Ïä§ÎßàÌä∏ Í≥ÑÏïΩÏóêÏÑú Î∏îÎ°ù Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchBlocks = async (contract, account) => {
    try {
      if (contract && account) {
        const blockCount = await contract.methods.getIndexOf(account).call();
        if (blockCount.length === 0n) {
          console.log("No blocks available.");
          return;
        }

        const fetchedBlockResults = [];

        for (let i = 0; i < blockCount.length; i++) {
          const block = await contract.methods.getInfo(account, blockCount[i]).call();
          fetchedBlockResults.push({
            nickName: block[0],
            gameName: block[1],
            serverName: block[2],
            className: block[3],
            remarks: block[4],
            date: new Date(Number(block[5]) * 1000).toLocaleString(),
          });
          if (i + 1 === blockCount.length) setNickName(block[0]);
        }

        setBlocks(fetchedBlockResults.reverse());
      }
    } catch (error) {
      console.error("Î∏îÎ°ù Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    }
  };

  // ‚úÖ Ïú†Ï†Ä ÏÑ†ÌÉù Ïãú Ïã§Ìñâ (StatsÏóêÏÑú ÌÅ¥Î¶≠ÌïòÎ©¥ Ïã§ÌñâÎê®)
  const handleUserSelect = (userAddress, userNickName) => {
    setSelectedUser({ userAddress, userNickName });
    fetchSearchedBlocks(userAddress);
  };

  // üîπ ÌäπÏ†ï Ïú†Ï†ÄÏùò Î∏îÎ°ù Í≤ÄÏÉâ (ÎãâÎÑ§ÏûÑ Í∏∞Î∞ò)
  const fetchSearchedBlocks = async (account) => {
    try {
      if (contract && account) {
        const blockCount = await contract.methods.getIndexOf(account).call();
        if (blockCount.length === 0n) {
          console.log("No blocks available.");
          return;
        }

        const fetchedBlockResults = [];

        for (let i = 0; i < blockCount.length; i++) {
          const block = await contract.methods.getInfo(account, blockCount[i]).call();
          fetchedBlockResults.push({
            nickName: block[0],
            gameName: block[1],
            serverName: block[2],
            className: block[3],
            remarks: block[4],
            date: new Date(Number(block[5]) * 1000).toLocaleString(),
          });
        }

        setSearchedBlocks(fetchedBlockResults.reverse());
      }
    } catch (error) {
      console.error("Î∏îÎ°ù Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    }
  };

  // üîπ Í≤ÄÏÉâ ÌôîÎ©¥ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
  const handleBackToSearch = () => {
    setSelectedUser(null);
  };

  return (
    <div className="main-container">
      <Header />
      <h1 style={{ textAlign: "center", marginBottom: "15px", marginTop: "15px" }}>
        Welcome, {nickName}!
      </h1>

      {!selectedUser ? (
        <Search contract={contract} onUserSelect={handleUserSelect} />
      ) : (
        <div className="user-history">
          <h2>
            {selectedUser.userNickName}ÎãòÏùò Í∏∞Î°ù
            <button onClick={handleBackToSearch} className="back-button">Îí§Î°ú</button>
          </h2>
          <History blocks={searchedBlock} />
        </div>
      )}

      {!selectedUser && (
        <>
          <CreateBlock contract={contract} account={account} blocks={blocks} setBlocks={setBlocks} fetchBlocks={fetchBlocks} />
          <History blocks={blocks} />
          <Stats onUserSelect={handleUserSelect} />
        </>
      )}

      <Footer />
      <div className="button-container">
        <ChatBTN nickName={nickName} />
        <HelpBTN />
      </div>
    </div>
  );
};

export default App;
