import React from 'react';
import '../css/history.css';
import { useState, useEffect } from 'react';
import { ChatBTN } from './Buttons';


export function History({ blocks }) {
  console.log(blocks.nickName);
  return (
    <div className="con-history">
      {blocks.length > 0 ? (
        blocks.map((block) => (
          <div key={block.id}>
            <table className="his-con-table">
              <thead>
                <tr className="his-title-date">
                  <td></td>
                  <td className="his-title">
                    <span className="his-title-data">
                      {block.nickName}님의 {block.gameName} / {block.serverName} / {block.className} 접속 기록
                    </span>
                  </td>
                  <td>{block.date}</td> {/* 날짜는 그대로 출력 */}
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td rowSpan="2" className="sub-img"></td>
                  <td colSpan="2">
                    <textarea
                      disabled
                      rows="5"
                      className="show-comment"
                      value={block.remarks}
                    />
                  </td>
                </tr>
              </tbody>
            </table>

            <ChatBTN nickName={block.nickName}/>

          </div>
        ))
      ) : (
        <p>No blocks available.</p> // 블록이 없을 경우 안내 메시지 표시
      )}
    </div>
  );
}

export default History;
