import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { History } from "./pages/History";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import "./App.css";
import { Search } from "./pages/Search";
import IdProject from "./IdProject.json";
import CreateBlock from "./pages/CreateBlock";
import Stats from "./pages/Stats";
const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]); // Î∏îÎ°ù ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [selectedUser, setSelectedUser] = useState(null); // ÏÑ†ÌÉùÎêú ÏÇ¨Ïö©Ïûê
  const [searchedBlock, setSearchedBlocks] = useState([]);
  const [nickName, setNickName] = useState("");

  // Î©îÌÉÄÎßàÏä§ÌÅ¨ Î∞è Ïä§ÎßàÌä∏ Í≥ÑÏïΩ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          if (accounts.length > 0) {
            const account = accounts[0];
            setAccount(account);
            const networkId = await web3.eth.net.getId();
            console.log(networkId);
            const deployedNetwork = IdProject.networks[networkId];
            console.log(deployedNetwork.address);
            const instance = new web3.eth.Contract(
              IdProject.abi,
              deployedNetwork.address
            );

            setContract(instance);

            // Í≥ÑÏïΩÏóêÏÑú ÏßÅÏ†ë Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
            fetchBlocks(instance, account);
          } else {
            console.error("MetaMask Í≥ÑÏ†ïÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          }
        } catch (error) {
          console.error("Web3 Ï¥àÍ∏∞Ìôî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
          alert(
            "MetaMaskÏôÄÏùò Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. MetaMaskÍ∞Ä ÏÑ§ÏπòÎêòÏñ¥ ÏûàÍ≥†, Í≥ÑÏ†ïÏù¥ Ïó∞Í≤∞ÎêòÏóàÎäîÏßÄ ÌôïÏù∏Ìï¥ Ï£ºÏÑ∏Ïöî."
          );
        }
      } else {
        console.error("Ethereum object not found. Please install MetaMask.");
        alert("MetaMaskÍ∞Ä ÏÑ§ÏπòÎêòÏñ¥ ÏûàÏßÄ ÏïäÏäµÎãàÎã§. MetaMaskÎ•º ÏÑ§ÏπòÌï¥ Ï£ºÏÑ∏Ïöî.");
      }
    };
    initWeb3();
    // ÏÉàÎ°úÍ≥†Ïπ® Ïãú Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú blocks Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
    const savedBlocks = localStorage.getItem("blocks");
    if (savedBlocks) {
      setBlocks(JSON.parse(savedBlocks));
    }
  }, []);

  // blocks ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Ïãú Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê Ï†ÄÏû•
  useEffect(() => {
    if (blocks.length > 0) {
      localStorage.setItem("blocks", JSON.stringify(blocks));
    }
  }, [blocks]);

  // Î∏îÎ°ù Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ (Ïä§ÎßàÌä∏ Í≥ÑÏïΩÏóêÏÑú Ïã§ÏãúÍ∞ÑÏúºÎ°ú Î∏îÎ°ù Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞)
  const fetchBlocks = async (contract, account) => {
    try {
      if (contract && account) {
        // blockCountÎäî BigIntÎ°ú Î∞òÌôòÎê† Ïàò ÏûàÏúºÎØÄÎ°ú, Ïù¥Î•º BigIntÎ°ú Ï≤òÎ¶¨Ìï©ÎãàÎã§.
        const blockCount = await contract.methods.getIndexOf(account).call();

        if (blockCount.length === 0n) {
          // blockCountÍ∞Ä BigIntÏù¥ÎØÄÎ°ú ÎπÑÍµêÌï† ÎïåÎèÑ BigIntÎ°ú ÎπÑÍµê
          console.log("No blocks available.");
          return;
        }

        const fetchedBlockResults = [];

        // iÎäî BigIntÎ°ú Ï≤òÎ¶¨Ìï©ÎãàÎã§. (1nÏùÄ BigInt Í∞í)
        for (let i = 0; i < blockCount.length; i++) {
          const block = await contract.methods
            .getInfo(account, blockCount[i])
            .call();
          console.log(`Block ${i}:`, block);

          fetchedBlockResults.push({
            // blockId: blockCount[i].toString(), // BigIntÎ•º stringÏúºÎ°ú Î≥ÄÌôòÌïòÏó¨ Ï†ÄÏû•
            nickName: block[0],
            gameName: block[1],
            serverName: block[2],
            className: block[3],
            remarks: block[4],
            date: new Date(Number(block[5]) * 1000).toLocaleString(), // ÌÉÄÏûÑÏä§ÌÉ¨ÌîÑ Î≥ÄÌôò
          });
          if (i + 1 === blockCount.length) setNickName(block[0]);
        }

        setBlocks(fetchedBlockResults.reverse()); // Î∏îÎ°ù ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      }
    } catch (error) {
      console.error("Î∏îÎ°ù Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    }
  };

  const handleUserSelect = (user) => {
    if (!user || !user.address) {
      console.error("User address is undefined!"); // üö® ÎîîÎ≤ÑÍπÖ Î°úÍ∑∏ Ï∂îÍ∞Ä
      return;
    }

    console.log("Selected User Address:", user.address); // üîç Ï†úÎåÄÎ°ú ÎÑòÏñ¥Ïò§ÎäîÏßÄ ÌôïÏù∏
    setSelectedUser(user); // user Í∞ùÏ≤¥ Ï†ÑÏ≤¥ Ï†ÄÏû•
    fetchSearchedBlocks(user.address); // addressÎßå ÎÑòÍ∏∞Î©¥ Îê®
  };


  const fetchSearchedBlocks = async (userAddress) => {
    if (!userAddress || !Web3.utils.isAddress(userAddress)) {
      console.error("Invalid Ethereum address:", userAddress);
      return;
    }

    try {
      console.log("Fetching blocks for:", userAddress);
      const blockCount = await contract.methods.getIndexOf(userAddress).call();

      if (blockCount.length === 0n) {
        console.log("No blocks available for this user.");
        setSearchedBlocks([]); 
        return;
      }

      const fetchedBlockResults = [];

      for (let i = 0; i < blockCount.length; i++) {
        const block = await contract.methods.getInfo(userAddress, blockCount[i]).call();
        console.log(`Block ${i}:`, block);

        fetchedBlockResults.push({
          nickName: block[0],
          gameName: block[1],
          serverName: block[2],
          className: block[3],
          remarks: block[4],
          date: new Date(Number(block[5]) * 1000).toLocaleString(),
        });
      }

      setSearchedBlocks(fetchedBlockResults.reverse());
    } catch (error) {
      console.error("Error fetching blocks:", error);
    }
  };


  // ÏÇ¨Ïö©Ïûê ÏÑ†ÌÉù Ï∑®ÏÜå (Í≤ÄÏÉâ ÌôîÎ©¥ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞)
  const handleBackToSearch = () => {
    setSelectedUser(null);
  };

  return (
    <div className="main-container">
      <Header />
      <h1
        style={{
          textAlign: "center",
          margin: 0,
          padding: 0,
          marginBottom: "15px",
          marginTop: "15px",
        }}
      >
        {" "}
        Welcome, {nickName}!{" "}
      </h1>
      {!selectedUser ? (
        // Í≤ÄÏÉâ ÌôîÎ©¥
        <Search contract={contract} onUserSelect={handleUserSelect} />
      ) : (
        // ÏÑ†ÌÉùÎêú ÏÇ¨Ïö©Ïûê ÌûàÏä§ÌÜ†Î¶¨ ÌôîÎ©¥
        <div className="user-history">
          <h2>{selectedUser.nickName}ÎãòÏùò Í∏∞Î°ù <button onClick={handleBackToSearch} className="back-button"></button></h2>
          <History blocks={searchedBlock} />
        </div>
      )}
      {!selectedUser && (
        <>
          {/* Î∏îÎ°ù ÏÉùÏÑ± Î∞è Ï†ÑÏ≤¥ Î∏îÎ°ù ÌûàÏä§ÌÜ†Î¶¨ */}
          <CreateBlock
            contract={contract}
            account={account}
            blocks={blocks}
            setBlocks={setBlocks}
            fetchBlocks={fetchBlocks}
          />
          <History blocks={blocks} />
          <Stats contract={contract}/>
        </>
      )}
      <Footer />
      <div className="button-container">
        <ChatBTN nickName={nickName} />
        <HelpBTN />
      </div>
    </div>
  );
};

export default App;
