import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { History } from "./pages/History";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import './App.css';
import IdProject from "./IdProject.json";


const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [gameName, setGameName] = useState("");
  const [serverName, setServerName] = useState("");
  const [className, setClassName] = useState("");
  const [remarks, setRemarks] = useState("");

  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        alert("먼저 MetaMask 로그인 해주세요");
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          const networkId = await web3.eth.net.getId();
          const deployedNetwork = IdProject.networks[networkId];
          const instance = new web3.eth.Contract(IdProject.abi,
            deployedNetwork && deployedNetwork.address);

          setAccount(accounts[0]);
          setContract(instance);
          getInfo(instance);
        } catch (error) {
          console.error("User have to Login", error);
        }
      } else{
        console.log("Please install MetaMask!");
      }
    };
    initWeb3();
  }, []);

  const getInfo = async (contract) => {
    const blockCount = await contract.methods.blockCount().call();
    const gotInfos = [];

    for (let i = 1; i <= blockCount; i++) {
      const block = await contract.methods.getInfo(i).call();
      gotInfos.push(block);
    }
    setBlocks(gotInfos);
  };

  const insertInfo = async () => {


    await contract.methods.insertInfo(gameName, serverName, className, remarks).send({ from: account, gas: 3000000 });
    getInfo(contract);
  };




  return (
    <div className="main-container">
      <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH" crossorigin="anonymous" />
      <Header />
      <div className="block-create">
      <ul>
        {blocks.map((block) => (
          <li key={block.id}>
            {block.gameName} - {block.serverName} - {block.className} -{" "}
            {block.remarks}
          </li>
        ))}
      </ul><div className="contents-box">
      <table class="main-contents">
  <tr>
    <td class="con-title"><span class="input-group-text" id="basic-addon1">게임명</span></td>
    <td class="con-text"><input type="text" class="form-control" onChange={(e) => 
      setGameName(e.target.value)} placeholder="ex)로스트아크" /></td>
    <td rowspan="3">
    <span class="input-group-text">Note</span>
    <textarea class="form-control" aria-label="With textarea" rows="5" cols="50" onChange={(e) =>
       setRemarks(e.target.value)} placeholder="100byte 제한" />
    </td>
  </tr>
  <tr>
    <td class="con-title"><span class="input-group-text" id="basic-addon1">서버명</span></td>
    <td class="con-text"><input type="text" class="form-control" onChange={(e) => 
      setServerName(e.target.value)} placeholder="ex)루페온" /></td>
  </tr>
  <tr>
    <td class="con-title"><span class="input-group-text" id="basic-addon1">클래스</span></td>
    <td class="con-text"><input type="text" class="form-control" onChange={(e) => 
      setClassName(e.target.value)} placeholder="ex)슬레이어" /></td>
  </tr>
  <tr>
    <td colspan="3">
    <div class="d-grid gap-2">
  <button class="btn btn-outline-secondary" type="button" onClick={insertInfo} >
  자료 입력/블록 생성</button>
</div></td>
  </tr>
</table></div></div>
    <History/>
     <ChatBTN/><HelpBTN />
     <Footer />
    </div>
  );
};

export default App;
