import React, { useEffect, useState } from "react";
import "../css/intro.css";
import { Link, useNavigate } from 'react-router-dom';
import { ChatBTN, HelpBTN } from "./Buttons";

const Intro = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const checkMetaMaskLogin = async () => {
      if (window.ethereum) {
        try {
          const accounts = await window.ethereum.request({ method: 'eth_accounts' });
          if (accounts.length > 0) {
            setIsLoggedIn(true);
          } else {
            setIsLoggedIn(false);
          }
        } catch (error) {
          console.error("Error checking MetaMask login status:", error);
          setIsLoggedIn(false);
        }
      } else {
        console.error("MetaMask is not installed.");
        setIsLoggedIn(false);
      }
    };

    checkMetaMaskLogin();
  }, []);

  const handleStartClick = () => {
    if (isLoggedIn) {
      navigate("/app");
    } else {
      alert("MetaMask에 로그인되어 있지 않습니다. 로그인 후 다시 시도해주세요.");
    }
  };

  return (
    <div>
      <div className="main-container-int">
        <div className="main-img">
          <img src="/main02.png" alt="main" />
        </div>
        <div className="btn-group" role="group" aria-label="Basic mixed styles example">
          <Link to="/#">
            <button type="button" className="btn btn-tutorial" />
          </Link>
          <button type="button" className="btn btn-start" onClick={handleStartClick} />
        </div>
      </div>
      <ChatBTN />
      <HelpBTN />
    </div>
  );
};

export default Intro;
