import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { History } from "./pages/History";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import './App.css';
import { Search } from "./pages/Search";
import IdProject from "./IdProject.json";

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [nickname, setNickname] = useState("");


  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          if (accounts.length > 0) {
            const account = accounts[0];
            if (!Web3.utils.isAddress(account)) {
              console.error("Invalid account address.");
              return;
            }
            setAccount(account);
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = IdProject.networks[networkId];
            if (!deployedNetwork) {
              alert("Contract가 현재 네트워크에 배포되지 않았습니다.");
              return;
            }
            const instance = new web3.eth.Contract(IdProject.abi, deployedNetwork.address);
            setContract(instance);
            getInfo(instance, account);
            getNickname(instance, account); // 닉네임 가져오기 함수 호출
          } else {
            console.error("No accounts found.");
          }
        } catch (error) {
          console.error("Error initializing web3:", error);
        }
      } else {
        console.error("Ethereum object not found. Please install MetaMask.");
      }
    };
    initWeb3();
  }, []);

  const getInfo = async (contract, account) => {
    try {
      const blockCount = await contract.methods.blockCount().call();
      const gotInfos = [];
      for (let i = 1; i <= blockCount; i++) {
        const block = await contract.methods.getInfo(account, i).call();  // account 주소와 blockId (i)를 전달
        gotInfos.push(block);
      }
      setBlocks(gotInfos);
    } catch (error) {
      console.error("Error getting info:", error);
    }
  };

  const getNickname = async(contract, account) => {
    try{
      const nickname = await contract.methods.getNickname(account).call();
      setNickname(nickname);
    } catch(error){
      console.error("Error getting nickname:", error);
    }
  }


  return (
    <div className="main-container">
      <link
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css"
        rel="stylesheet"
        integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH"
        crossorigin="anonymous"
      /> 
      <Header />
      <Search />
      <CreateBlock 
      contract={contract} 
      account={account} 
      getInfo={getInfo} 
      blocks={blocks} />
      <History />
      <ChatBTN />
      <HelpBTN />
      <Footer />
    </div>
  );
};

export default App;
