import { useState, useEffect } from "react";
import "../css/stats.css";

export function Stats({ contract }) {
  const [totalUsers, setTotalUsers] = useState(0);
  const [totalBlocks, setTotalBlocks] = useState(0);
  const [recentUsers, setRecentUsers] = useState([]);

  useEffect(() => {
    if (!contract) return;

    const fetchStats = async () => {
      try {
        const users = await contract.methods.getTotalUserCount().call();
        const blocks = await contract.methods.getTotalBlockCount().call();
        const recent = await contract.methods.getRecentUser().call();

        setTotalUsers(users);
        setTotalBlocks(blocks);
        setRecentUsers(recent[0]); // 닉네임 배열만 저장
      } catch (error) {
        console.error("Error fetching stats:", error);
      }
    };

    fetchStats();
  }, [contract]);

  return (
    <>
      <table className="total-table">
        <tbody>
          <tr>
            <td className="stats-title">총 누적 유저</td>
            <td className="total-ctn">{totalUsers}</td>
          </tr>
          <tr>
            <td className="stats-title">총 누적 블록</td>
            <td className="total-ctn">{totalBlocks}</td>
          </tr>
        </tbody>
      </table>
      <div className="list-form">
        <p>최근 활동 유저</p>
        <div className="stats-list">
          {recentUsers.length > 0 ? (
            recentUsers.map((nickname, index) => (
              <div key={index}>
                <strong>{nickname}</strong>
              </div>
            ))
          ) : (
            <p>최근 활동한 유저 없음</p>
          )}
        </div>
      </div>
    </>
  );
}

export default Stats;
