import '../css/history.css';
import React, { useState, useEffect } from 'react';

export function History({ blocks }) {
  const [convertedDate, setConvertedDate] = useState('');

  useEffect(() => {
    const blockTimestamp = 1672531195; // 예시 타임스탬프, 실제 데이터로 변경 필요
    const date = new Date(blockTimestamp * 1000); // 밀리초 단위로 변환
    setConvertedDate(date.toLocaleString());
  }, []);

  return (
    <div className="con-history">
      <table className="his-con-table">
        {blocks.map((block) => (
          <tr className='his-title-date' key={block.id}>
            <td></td>
            <td className='his-title'>
              <span className='his-title-data'>
                [{block.gameName} : {block.serverName} ({block.className})] 속 [{block.nickName}] 님은
              </span>
            </td>
            <td>{convertedDate}</td>
          </tr>
        ))}
        <tr>
          <td rowSpan="2" className="sub-img"></td>
          <td colSpan='2'>
            <textarea disabled rows="5" className='show-comment'>
              주저리주저리 내가 오늘 물풍선을 막 쏴가지고 막 오쪼구조쪼구
            </textarea>
          </td>
        </tr>
      </table>
    </div>
  );
}

export default History;
