import { useEffect, useState } from "react";
import Web3 from "web3";
import IdProject from "../IdProject.json"; // Ïä§ÎßàÌä∏ Í≥ÑÏïΩ ABI ÌååÏùº
import "../css/stats.css";

export function Stats() {
  const [totalUsers, setTotalUsers] = useState(0);
  const [totalBlocks, setTotalBlocks] = useState(0);
  const [recentUsers, setRecentUsers] = useState([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        if (!window.ethereum) {
          console.error("MetaMask not detected");
          return;
        }

        const web3 = new Web3(window.ethereum);
        await window.ethereum.request({ method: "eth_requestAccounts" });
        const networkId = await web3.eth.net.getId();
        const deployedNetwork = IdProject.networks[networkId];
        const contract = new web3.eth.Contract(
          IdProject.abi,
          deployedNetwork.address
        );

        // Ïä§ÎßàÌä∏ Í≥ÑÏïΩÏóêÏÑú Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
        const userCount = await contract.methods.getTotalUserCount().call();
        const blockCount = await contract.methods.getTotalBlockCount().call();
        const recentUserList = await contract.methods.getRecentUser().call();

        console.log("ÏµúÍ∑ºÌôúÎèôÏú†Ï†Ä Îç∞Ïù¥ÌÑ∞ Íµ¨Ï°∞:", recentUserList);

        setTotalUsers(userCount);
        setTotalBlocks(blockCount);

        // üìå Î™®Îì† Ïú†Ï†Ä ÎãâÎÑ§ÏûÑÏùÑ ÌïòÎÇòÏùò Î∞∞Ïó¥Î°ú Î≥ÄÌôò
        const updatedRecentUsers = Object.values(recentUserList).flat();
        setRecentUsers(updatedRecentUsers);
      } catch (error) {
        console.error("Error fetching stats:", error);
      }
    };

    fetchStats();
  }, []);

  // ‚úÖ `recentUsers`Í∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ ÏΩòÏÜî Ï∂úÎ†•
  useEffect(() => {
    console.log("ÏóÖÎç∞Ïù¥Ìä∏Îêú ÏµúÍ∑º ÌôúÎèô Ïú†Ï†Ä:", recentUsers);
  }, [recentUsers]);

  return (
    <>
      <table className="total-table">
        <tbody>
          <tr>
            <td className="stats-title">Ï¥ù ÎàÑÏ†Å Ïú†Ï†Ä</td>
            <td className="total-ctn">{totalUsers}</td>
          </tr>
          <tr>
            <td className="stats-title">Ï¥ù ÎàÑÏ†Å Î∏îÎ°ù</td>
            <td className="total-ctn">{totalBlocks}</td>
          </tr>
        </tbody>
      </table>
      <div className="list-form">
        <p>ÏµúÍ∑º ÌôúÎèô Ïú†Ï†Ä</p>
        <div className="stats-list">
          {recentUsers.length > 0 ? (
            <div className="user-list">
              {recentUsers.map((nickname, index) => (
                <div key={index} className="user-item">
                  <span>{nickname}</span>
                </div>
              ))}
            </div>
          ) : (
            "Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå"
          )}
        </div>
      </div>
    </>
  );
}

export default Stats;
