export function ChatBTN(nickName) {
  console.log(nickName);
  return (
    <div className="flo-btn-chat">
      <form action="http://192.168.0.16:8000" method="POST">
        <input type="hidden" name="user" value={nickName} />
        <button className="flo flo-chat" type="submit"></button>
      </form>
    </div>
  );
}*/

import React from "react";

export function ChatBTN({ nickName }) {
  console.log("닉네임:", nickName); // 콘솔에서 닉네임 확인

  const sendData = async () => {
    if (!nickName) {
      console.error("닉네임이 없습니다!");
      return;
    }

    try {
      const response = await fetch("http://192.168.0.90:8000", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: new URLSearchParams({ user: nickName }),
      });

      if (!response.ok) {
        throw new Error(`서버 응답 오류: ${response.status}`);
      }

      const data = await response.text(); // 서버 응답 받기
      console.log("서버 응답:", data);
    } catch (error) {
      console.error("에러 발생:", error.message);
    }
  };

  return (
    <div className="flo-btn-chat">
      <button className="flo flo-chat" onClick={sendData}>전송</button>
    </div>
  );
}

export function HelpBTN() {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate('/help');
  };

  return (
    <div className="flo-btn-help">
      <button className="flo flo-help" onClick={handleClick}>
      </button>
    </div>
  );
}
