import React from 'react';
import '../css/history.css';
import { useState, useEffect } from 'react';

export function History({ blocks }) {
  const [convertedDate, setConvertedDate] = useState('');

  useEffect(() => {
    const blockTimestamp = 1672531195; // 예시 타임스탬프로 솔리디티랑 연결 필요 !
    const date = new Date(blockTimestamp * 1000); // 밀리초 단위로 변환
    setConvertedDate(date.toLocaleString());
  }, []);


  return (
    <div className="con-history">
      {blocks.length > 0 ? (
        blocks.map((block, index) => (
          <div key={index}>
            <table className="his-con-table">
              <tr className='his-title-date'>
                <td></td>
                <td className='his-title'>
                  <span className='his-title-data'>
                    [게임명: {block.gameName}, 서버명: {block.serverName}, 클래스: {block.className}]
                  </span>
                  {block.nickName} 님
                </td>
                <td>{convertedDate}</td> {/* 타임스탬프 데이터 추가 예정 */}
              </tr>
              <tr>
                <td rowSpan="2" className="sub-img"></td>
                <td colSpan="2">
                  <textarea 
                    disabled 
                    rows="5" 
                    className="show-comment" 
                    value={block.remarks}
                  />
                </td>
              </tr>
            </table>
          </div>
        ))
      ) : (
        <p>No blocks available.</p> // 블록이 없을 경우 안내 메시지 표시
      )}
    </div>
  );
}

export default History;
