import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import './App.css';

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [gameName, setGameName] = useState("");
  const [serverName, setServerName] = useState("");
  const [className, setClassName] = useState("");
  const [remarks, setRemarks] = useState("");

  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          //const networkId = await web3.eth.net.getId();
          //const deployedNetwork = ___.networks[networkId];
          const instance = new web3.eth.Contract();
          //___.abi,
          //deployedNetwork && deployedNetwork.address
          setAccount(accounts[0]);
          setContract(instance);
          getInfo(instance);
        } catch (error) {}
      }
    };
    initWeb3();
  }, []);

  const getInfo = async (contract) => {
    const blockCount = await contract.methods.blockCount().call();
    const gotInfos = [];
    for (let i = 1; i <= blockCount; i++) {
      const block = await contract.methods.getInfo(i).call();
      gotInfos.push(block);
    }
    setBlocks(gotInfos);
  };

  const insertInfo = async () => {
    await contract.methods
      .insertInfo(gameName, serverName, className, remarks)
      .send({ from: account, gas: 3000000 });
    getInfo(contract);
  };
  return (
    <div className="main-container">
      <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH" crossorigin="anonymous" />
      <Header />
      <div className="block-create">
      <ul>
        {blocks.map((block) => (
          <li key={block.id}>
            {block.gameName} - {block.serverName} - {block.className} -{" "}
            {block.remarks}
          </li>
        ))}
      </ul>
      <table class="tg"><thead>
  <tr>
    <th class="tg-0pky"><span class="input-group-text" id="basic-addon1">게임명</span></th>
    <th class="tg-0pky"><input type="text" class="form-control" onChange={(e) => setGameName(e.target.value)} placeholder="게임 이름" /><br/></th>
    <th class="tg-0pky" rowspan="3">
    <span class="input-group-text">Note</span>
    <textarea class="form-control" aria-label="With textarea" rows="10" cols="50" onChange={(e) => setRemarks(e.target.value)} placeholder="100byte 제한" />

    </th>
  </tr>
  <tr>
    <th class="tg-0pky"><span class="input-group-text" id="basic-addon1">서버명</span></th>
    <th class="tg-0pky"><input type="text" class="form-control" onChange={(e) => setServerName(e.target.value)} placeholder="서버 이름" /><br/></th>
  </tr>
  <tr>
    <th class="tg-0pky"><span class="input-group-text" id="basic-addon1">클래스</span></th>
    <th class="tg-0pky"><input type="text" class="form-control" onChange={(e) => setClassName(e.target.value)} placeholder="클래스" /><br/></th>
  </tr></thead>
<tbody>
  <tr>
    <td class="tg-0pky" colspan="3"><button onClick={insertInfo}>자료 입력/블록 생성</button></td>
  </tr>
</tbody>
</table>



  <span class="input-group-text" id="basic-addon1">게임명</span>
  <input type="text" class="form-control" onChange={(e) => setGameName(e.target.value)} placeholder="게임 이름" /><br/>
  <span class="input-group-text" id="basic-addon1">서버명</span>
  <input type="text" class="form-control" onChange={(e) => setServerName(e.target.value)} placeholder="서버 이름" /><br/>
  <span class="input-group-text" id="basic-addon1">클래스</span>
  <input type="text" class="form-control" onChange={(e) => setClassName(e.target.value)} placeholder="클래스" /><br/>

      <div class="input-group">
      <span class="input-group-text">Note</span>
      <textarea class="form-control" aria-label="With textarea" rows="10" cols="50" onChange={(e) => setRemarks(e.target.value)} placeholder="100byte 제한" />
      </div>
     <p><button onClick={insertInfo}>자료 입력/블록 생성</button></p> </div>
     <div classname="con-history"> </div>
     <ChatBTN/><HelpBTN />
    <Footer />
    </div>
  );
};

export default App;
