import "../css/intro.css";
import { Link, useNavigate } from 'react-router-dom';
import { ChatBTN, HelpBTN } from "./Buttons";
import Web3 from "web3";
import { errorMonitor } from "ws";


const Intro = () => {
  const navigate = useNavigate();

  // MetaMask 로그인 상태 확인 함수
  const checkMetaMaskLogin = async () => {
    if(window.ethereum){
      const web3 = new Web3(window.ethereum);
      try{
        const accounts = await web3.eth.getAccounts();
        return accounts.length > 0;
      } catch(error){
        console.error("MetaMask 로그인 상태 확인 중 오류 발생:", error);
        return false;
      }
    } else{
      console.error("MetaMask가 설치되어 있지 않습니다.");
      return false;
    }
  }


  const handleStartClick = async() => {

    const isLoggedIn = await checkMetaMaskLogin();
    if( isLoggedIn ){
      navigate("/app");
    } else{
      alert("MetaMask에 로그인 되어 있지 않습니다.")
    }    
  };

  return (
    <div>
      <div className="main-container-int">
        <div className="main-img">
          <img src="/main02.png" alt="main" />
        </div>
        <div className="btn-group" role="group" aria-label="Basic mixed styles example">
          <Link to="/#">
            <button type="button" className="btn btn-tutorial" />
          </Link>
          <button type="button" className="btn btn-start" onClick={handleStartClick}/>
        </div>
      </div>
      <ChatBTN />
      <HelpBTN />
    </div>
  );
}



export default Intro;
