import { useNavigate } from 'react-router-dom';
import '../css/buttons.css'; // CSS 파일 가져오기
import IdProject from "../IdProject.json";
import App from "../App";

export function ChatBTN({ blocks }) {
  const handleClick = () => {
    if (blocks && blocks.nickName) {
      // 닉네임을 URL에 포함
      const nickname = encodeURIComponent(blocks.nickName); // URL 안전하게 인코딩
      window.location.href = `http://localhost:8000/?nickname=${nickname}`;
    } else {
      console.error("닉네임이 제공되지 않았습니다."); // 닉네임이 없을 경우 로그 출력
    }
  };

  return (
    <div className="flo-btn-chat">
      <button className="flo flo-chat" onClick={handleClick}>
   
      </button>
    </div>
  );
}

export function HelpBTN() {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate('/help');
  };

  return (
    <div className="flo-btn-help">
      <button className="flo flo-help" onClick={handleClick}>
      </button>
    </div>
  );
}
