import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { History } from "./pages/History";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import './App.css';
import { Search } from "./pages/Search";
import IdProject from "./IdProject.json";

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [nickname, setNickname] = useState(""); // 닉네임 상태 추가
  const [gameName, setGameName] = useState("");
  const [serverName, setServerName] = useState("");
  const [className, setClassName] = useState("");
  const [remarks, setRemarks] = useState("");

  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          const networkId = await web3.eth.net.getId();
          const deployedNetwork = IdProject.networks[networkId];
          const instance = new web3.eth.Contract(IdProject.abi, deployedNetwork && deployedNetwork.address);
          setAccount(accounts[0]);
          setContract(instance);
          getInfo(instance);
          fetchNickname(instance, accounts[0]); // 닉네임 가져오기 호출
        } catch (error) {
          console.error("Error initializing web3:", error);
        }
      }
    };
    initWeb3();
  }, []);

  const getInfo = async (contract) => {
    const blockCount = await contract.methods.blockCount().call();
    const gotInfos = [];
    for (let i = 1; i <= blockCount; i++) {
      const block = await contract.methods.getInfo(account, i).call();
      gotInfos.push(block);
    }
    setBlocks(gotInfos);
  };

  const fetchNickname = async (contract, account) => {
    try {
      const userNickname = await contract.methods.getNickname(account).call();
      setNickname(userNickname);
    } catch (error) {
      console.error("Error fetching nickname:", error);
    }
  };

  const insertInfo = async () => {
    await contract.methods
      .insertInfo(nickname, gameName, serverName, className, remarks)
      .send({ from: account, gas: 3000000 });
    getInfo(contract);
  };

  return (
    <div className="main-container">
      <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH" crossorigin="anonymous" />
      <Header />
      <Search />
      <div className="block-create">
        <h4>Welcome, {nickname || "User"}!</h4> {/* 닉네임 표시 */}
        <ul>
          {blocks.map((block, index) => (
            <li key={index}>
              {block[0]} - {block[1]} - {block[2]} - {block[3]}
            </li>
          ))}
        </ul>
        <div className="contents-box">
          <table className="main-contents">
            <tr>
              <td className="con-title"><span className="input-group-text" id="basic-addon1">닉네임</span></td>
              <td className="con-text"><input type="text" className="form-control" value={nickname} readOnly /></td>
              <td rowSpan="4">
                <span className="input-group-text">Note</span>
                <textarea className="form-control" aria-label="With textarea" rows="7" cols="50" onChange={(e) =>
                  setRemarks(e.target.value)} placeholder="100byte 제한" />
              </td>
            </tr>
            <tr>
              <td className="con-title"><span className="input-group-text" id="basic-addon1">게임명</span></td>
              <td className="con-text"><input type="text" className="form-control" onChange={(e) =>
                setGameName(e.target.value)} placeholder="ex)로스트아크" /></td>
            </tr>
            <tr>
              <td className="con-title"><span className="input-group-text" id="basic-addon1">서버명</span></td>
              <td className="con-text"><input type="text" className="form-control" onChange={(e) =>
                setServerName(e.target.value)} placeholder="ex)루페온" /></td>
            </tr>
            <tr>
              <td className="con-title"><span className="input-group-text" id="basic-addon1">클래스</span></td>
              <td className="con-text"><input type="text" className="form-control" onChange={(e) =>
                setClassName(e.target.value)} placeholder="ex)슬레이어" /></td>
            </tr>
            <tr>
              <td colSpan="3">
                <div className="d-grid gap-2">
                  <button className="btn btn-outline-secondary" type="button" onClick={insertInfo} >
                    자료 입력/블록 생성
                  </button>
                </div>
              </td>
            </tr>
          </table>
        </div>
      </div>
      <History />
      <ChatBTN />
      <HelpBTN />
      <Footer />
    </div>
  );
};

export default App;
