import React, { useState, useEffect } from "react";
import Web3 from "web3";
import Header from "./pages/Header";
import Footer from "./pages/Footer";
import { History } from "./pages/History";
import { ChatBTN, HelpBTN } from "./pages/Buttons";
import './App.css';
import { Search } from "./pages/Search";

const App = () => {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [blocks, setBlocks] = useState([]);
  const [nickname, setNickname] = useState(""); // nickname으로 변경
  const [gameName, setGameName] = useState("");
  const [serverName, setServerName] = useState("");
  const [className, setClassName] = useState("");
  const [remarks, setRemarks] = useState("");

  useEffect(() => {
    const initWeb3 = async () => {
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: "eth_requestAccounts" });
          const accounts = await web3.eth.getAccounts();
          const instance = new web3.eth.Contract(contractABI, contractAddress);
          setAccount(accounts[0]);
          setContract(instance);
          getInfo(instance);
        } catch (error) {
          console.error("Error connecting to web3:", error);
        }
      }
    };
    initWeb3();
  }, []);

  const getInfo = async (contract) => {
    try {
      const blockCount = await contract.methods.blockCount().call();
      const gotInfos = [];
      for (let i = 1; i <= blockCount; i++) {
        const block = await contract.methods.getInfo(i).call();
        gotInfos.push(block);
      }
      setBlocks(gotInfos);
    } catch (error) {
      console.error("Error fetching block data:", error);
    }
  };

  const insertInfo = async () => {
    // 필수 필드가 비어있는지 체크
    if (!gameName || !serverName || !className || !remarks || !nickname) {
      alert("모든 필드를 입력해야 합니다.");
      return;
    }
    try {
      await contract.methods
        .insertInfo(gameName, serverName, className, remarks)
        .send({ from: account, gas: 3000000 });
      getInfo(contract); // 블록 정보를 다시 가져옴
    } catch (error) {
      console.error("Error inserting data into the contract:", error);
    }
  };

  return (
    <div className="main-container">
      <link
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css"
        rel="stylesheet"
        integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH"
        crossOrigin="anonymous"
      />
      <Header />
      <Search />
      <div className="block-create">
        <ul>
          {blocks.map((block, index) => (
            <li key={index}>
              {block.gameName} - {block.serverName} - {block.className} - {block.remarks}
            </li>
          ))}
        </ul>
        <div className="contents-box">
          <table className="main-contents">
            <tbody>
              <tr>
                <td className="con-title"><span className="input-group-text">닉네임</span></td>
                <td className="con-text"><input type="text" className="form-control" onChange={(e) => setNickname(e.target.value)} /></td>
                <td rowSpan="4">
                  <span className="input-group-text">Note</span>
                  <textarea className="form-control" aria-label="With textarea" rows="7" cols="50" onChange={(e) => setRemarks(e.target.value)} placeholder="100byte 제한" />
                </td>
              </tr>
              <tr>
                <td className="con-title"><span className="input-group-text">게임명</span></td>
                <td className="con-text"><input type="text" className="form-control" onChange={(e) => setGameName(e.target.value)} placeholder="ex)로스트아크" /></td>
              </tr>
              <tr>
                <td className="con-title"><span className="input-group-text">서버명</span></td>
                <td className="con-text"><input type="text" className="form-control" onChange={(e) => setServerName(e.target.value)} placeholder="ex)루페온" /></td>
              </tr>
              <tr>
                <td className="con-title"><span className="input-group-text">클래스</span></td>
                <td className="con-text"><input type="text" className="form-control" onChange={(e) => setClassName(e.target.value)} placeholder="ex)슬레이어" /></td>
              </tr>
              <tr>
                <td colSpan="3">
                  <div className="d-grid gap-2">
                    <button className="btn btn-outline-secondary" type="button" onClick={insertInfo}>
                      자료 입력/블록 생성
                    </button>
                  </div>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
      <History />
      <ChatBTN />
      <HelpBTN />
      <Footer />
    </div>
  );
};

export default App;
