import '../css/history.css'
import React, { useState, useEffect } from 'react';
import IdProject from '../IdProject.json';
import { Contract } from 'web3';

export function History() {
  const [convertedDate, setConvertedDate] = useState('');
  const [blocks, setBlocks] = useState([]);
  useEffect(() => {
    const blockTimestamp = 1672531195; // 예시 타임스탬프로 솔리디티랑 연결 필요 !
    const date = new Date(blockTimestamp * 1000); // 밀리초 단위로 변환
    setConvertedDate(date.toLocaleString());

    const getInfo = async(contract, account) => {
      const blockCount = await contract.methods.blockCount().call();
      const gotInfos = [];

      for(let i=1; i<=blockCount; i++){
        const block = await contract.methods.getInfo(account, i).call();
        gotInfos.push(block);
      }

      setBlocks(gotInfos);


    }
  }, []);

return (
<div className="con-history">
  


  <table class="his-con-table">
     <tr className='his-title-date'>
      <td></td>
    <td className='his-title'>
      <span className='his-title-data'>
        [게임명 : 서버명(클래스)] 속 [닉네임] 님은
      </span>
      {blocks.nickName}
    </td>
    <td> {convertedDate} </td>
    </tr>
    <tr>
      <td rowspan="2" className="sub-img"></td>
      <td colspan='2'>
        <textarea disabled rows="5" className='show-comment'>
          주저리주저리 내가 오늘 물풍선을 막 쏴가지고 막 오쪼구조쪼구
        </textarea>
      </td>
    </tr>
  </table>
</div>


);
}

export default History;
